import React from "react";

function ShopItem({item}) {

    return (
        <li className="shop-item" >
                <p>{item.name}</p>
                <p>{item.flavor_text}</p>
                <p>Cost {item.value} gold!</p>
                <button>Buy</button>
            </li>
    )
}

export default ShopItem
