import React from "react";
import CharacterLink from "./CharacterLinks";

function ProfileBar({selectCharacter, users, currentUser, handleCurrentUser})
{

    let characterLinks = currentUser.characters.map(character => <CharacterLink character={character}/>)

    function setupCharSelect() {
        currentUser.characters.map()
    }


    return(
        <div className="user-display">

            <h2>Welcome back, {currentUser.username}!</h2>
            <ul>{characterLinks}</ul>
            <div className="player-icon">
                <img src={currentUser != null ? currentUser.img_url : ""} alt="user image" />
                <p>{currentUser.username}</p>
                <button>Log Out</button>
            </div>
        </div>
    )
}

export default ProfileBar
