import React from "react";

function Spell({currentCharacter, spell}) {

    return (
        <div className="single">
            <h4>{spell.name}</h4>
            <p>{spell.value}</p>
            <p>{spell.flavor_text}</p>
            {currentCharacter.spell ? currentCharacter.spells.includes(spell) ? <button>Learned</button> : <button>Learn</button> : null}
        </div>
    )
}

export default Spell
