import React from "react";

function ShopItem({character, buying, item}) {

    function handleBuy(e) {
        e.preventDefault()

        if (character.gold >= item.value)
        {
            character.items.push(item)
            character.gold -= item.gold
            fetch(`http://localhost:9292/shop/buy/${character.id}/${item.id}`)
        }
        else
        {
            console.log("Broke")
        }

    }

    return (
        <div className="shop-item" >
            <p>{item.name}</p>
            <p>{item.flavor_text}</p>
            <p>Cost {item.value} gold!</p>
            {buying ? <button>Buy</button> : <button>Sell</button>}
        </div>
    )
}

export default ShopItem
