import React, {useEffect, useState} from "react";

function Shop() {

    const [items, setItems] = useState([])

    useEffect(() => {
        fetch("http://localhost:9292/items")
        .then(res => res.json())
        .then(setItems)
    }, [])

    function makeShopTable() {
        return items.map(item => {
            return <div className="shop-item" key={item.name + item.id}>
                <p>{item.name}</p>
                <p>{item.flavor_text}</p>
            </div>
        })
    }

    return (

        <div>
            {items.length > 0 ? makeShopTable() : null}
        </div>
    )
}

export default Shop
