import React, {useEffect, useState} from "react";
import ShopItem from "./ShopItem";

function Shop({currentCharacter}) {

    const [items, setItems] = useState([])
    const [buyItems, setBuyItems] = useState(true)

    useEffect(() => {
        fetch("http://localhost:9292/items")
        .then(res => res.json())
        .then(setItems)
    }, [])

    function makeBuyTable() {
        return items.map(item => <ShopItem key={item.name + item.id} character={currentCharacter} buying={true} item={item} />)
    }

    function makeSellTable(){
        return currentCharacter.items.map(item => <ShopItem key={item.name + item.id} character={currentCharacter} buying={false} item={item} />)
    }

    return (

        <div className="shop">
            {currentCharacter != null ? 
                <div>
                    <h1>Welcome {currentCharacter.name}</h1>
                    <h3>Would you like to:</h3>
                    <button onClick={() => setBuyItems(true)} >BUY ITEMS</button>
                    <button onClick={() => setBuyItems(false)}>SELL IEMS</button>

                    <ul >{items.length > 0 && buyItems ? makeBuyTable() : makeSellTable()}</ul>
                </div>
            : null}
        </div>
    )
}

export default Shop
