import React, { useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";
import "../App.css";
import Navbar from "./NavBar";
import Home from "./Home";
import Town from "./Town";
import ProfileBar from "./ProfileBar";
import CreateUserForm from "./CreateUserForm";

function App() {

    const [users, setUsers] = useState([])
    const [currentUser, setCurrentUser] = useState()
    const [newUsername, setUsername] = useState("")
    const [newPassword, setPassword] = useState("")
    const [newProfileImg, setProfileImg] = useState("")


    useEffect(() => {
        fetch("http://localhost:9292/users")
        .then(res => res.json())
        .then(setUsers)
    }, [])

    // function first_login() {
    //     alert("Welcome to React-ion! Please sign up to start your quest for the fabled ruby!")

    // }
    //User Functions
    function handleCurrentUser(user)
    {
        fetch(`http://localhost:9292/user/${user.id}/log_in`)
        .then(res => res.json())
        .then(userF => {
            setCurrentUser(userF)
            let tempUsers = [...users]
            return tempUsers.map(userM => {
                if (userM.id == userF.id) 
                {
                    userM.last_logged_in = userF.last_logged_in
                }
                return userM
            })
        })

    }

    function handleUpdateFormUsername(e) {
        setUsername(e.target.value)
    }

    function handleUpdateFormPassword(e) {
        setPassword(e.target.value)
    }

    function handleUpdateFormProfileImg(e) {
        setProfileImg(e.target.value)
    }


    return(
        <div className={`App`}>
            <Navbar/>
            {users.length > 0 ? <ProfileBar users={users} currentUser={currentUser} handleCurrentUser={handleCurrentUser}/> :
            <CreateUserForm onChangeUsername={handleUpdateFormUsername} newonChangePassword={handleUpdateFormPassword} newProfileImg={newProfileImg} onChangeProfileImg={handleUpdateFormProfileImg}/>}

            <Routes>
                <Route path="/" element={<Home/>} />
                <Route path="/town" element={<Town/>} />
            </Routes>
        </div>
    )
}

export default App
