import React, {useEffect, useState} from "react";
import ShopItem from "./ShopItem";

function Shop({currentUser}) {

    const [items, setItems] = useState([])

    useEffect(() => {
        fetch("http://localhost:9292/items")
        .then(res => res.json())
        .then(setItems)
    }, [])

    function makeShopTable() {
        return items.map(item => <ShopItem key={item.name + item.id} item={item} />)
    }

    return (

        <div className="shop">
            <h1>Welcome {currentUser.username}</h1>
            {items.length > 0 ? makeShopTable() : null}
        </div>
    )
}

export default Shop
