import React from "react";
import CharacterLink from "./CharacterLinks";

function ProfileBar({users, currentUser, handleCurrentUser})
{

    characterLinks = currentUser.characters.map(character => <CharacterLink character={character}/>)




    return(
        <div className="user-display">

            <h2>Welcome back, {currentUser.username}!</h2>
            <ul>{characterLinks}</ul>
            <div className="player-icon">
                <img src={currentUser != null ? currentUser.img_url : ""} alt="user image" />
            </div>
        </div>
    )
}

export default ProfileBar
